      const whereClause = {};
      if (filter) {
        const filterArray = JSON.parse(filter);
        if (Array.isArray(filterArray)) {
          filterArray.forEach(({
            operator,
            value,
            property
          }) => {
            if (operator && value && property) {
              whereClause[property] = {
                [Op[operator]]: value
              };
            }
          });
        }
      }
      const order = [];
      if (sort && typeof sort === "object") {
        const {
          property,
          direction
        } = sort;
        const sortDirection = direction === "desc" ? "DESC" : "ASC";
        if (property) {
          order.push([property, sortDirection]);
        }
      }
      const roomList = await db.Rooms.findAll({
        attributes: ["id", "roomType", "roomNumber", "price", "availability", "createdAt", "updatedAt"],
        where: whereClause,
        order,
        offset: start ? parseInt(start, 10) : undefined,
        limit: limit ? parseInt(limit, 10) : undefined
      });
      resolve(roomList);
    } catch (e) {
      reject(e);
    }
  });
};
