const db = require("../models/index");
const ExcelJS = require("exceljs");
const { Op } = require("sequelize");

const generateExcelData = async (filter) => {
  const whereClause = {};

  if (filter) {
    const filterArray = JSON.parse(filter);
    if (Array.isArray(filterArray)) {
      filterArray.forEach(({ operator, value, property }) => {
        if (operator && value && property) {
          whereClause[property] = {
            [Op[operator]]: value,
          };
        }
      });
    }
  }

  const filterRooms = await db.Rooms.findAll({ where: whereClause });

  const workbook = new ExcelJS.Workbook();
  const worksheet = workbook.addWorksheet("Rooms");

  if (filterRooms) {
    worksheet.columns = [
      { header: "Tn phng", key: "roomName", width: 300 },
