};
const getAllServices = ({
  start,
  limit,
  sort,
  filter
}) => {
  return new Promise(async (resolve, reject) => {
    try {
      const whereClause = {};
      if (filter) {
        const filterArray = JSON.parse(filter);
        if (Array.isArray(filterArray)) {
          filterArray.forEach(({
            operator,
            value,
            property
          }) => {
            if (operator && value && property) {
              whereClause[property] = {
                [Op[operator]]: value
              };
            }
          });
        }
      }
      const order = [];
      if (sort && typeof sort === "object") {
        const {
          property,
          direction
        } = sort;
        const sortDirection = direction === "desc" ? "DESC" : "ASC";
        if (property) {
          order.push([property, sortDirection]);
        }
      }
      const queryOptions = {
        attributes: ["id", "name", "createdAt", "updatedAt"],
        where: whereClause,
        order
      };
      if (start) {
        queryOptions.offset = parseInt(start, 10);
      }
      if (limit) {
        queryOptions.limit = parseInt(limit, 10);
      }
      const serviceList = await db.Services.findAndCountAll(queryOptions);
      resolve({
        data: serviceList.rows,
        count: serviceList.count
      });
    } catch (error) {
      reject(error);
    }
  });
};
