const db = require("../models/index");
const {
  Op
} = require("sequelize");
const createRoom = async data => {
  return new Promise(async (resolve, reject) => {
    console.log(data);
    try {
      const roomItem = await db.Rooms.create({
        roomType: data.roomType,
        roomNumber: data.roomNumber,
        price: data.price,
        availability: data.availability,
        services: data.services,
        imageRoom: data.imageRoom,
        optionGuestAndChildNumber: data.optionGuestAndChildNumber,
        roomName: data.roomName,
        roomEveduate: data.roomEveduate,
        checkIn: data.checkIn,
        checkOut: data.checkOut,
        isStayed: data.isStayed,
        isCheckOut: data.isCheckOut,
        monthCheckIn: data.monthCheckIn,
        monthCheckOut: data.monthCheckOut
      });
      resolve(roomItem);
    } catch (e) {
      reject(e);
    }
  });
};
const getAllRoom = ({
  start,
  limit,
  sort,
  filter
}) => {
  return new Promise(async (resolve, reject) => {
    try {
      const whereClause = {};
      if (filter) {
        const filterArray = JSON.parse(filter);
        if (Array.isArray(filterArray)) {
          filterArray.forEach(({
            operator,
            value,
            property
          }) => {
            if (operator && value && property) {
              whereClause[property] = {
                [Op[operator]]: value
              };
            }
          });
        }
      }
      const order = [];
      if (sort && typeof sort === "object") {
        const {
          property,
          direction
        } = sort;
        const sortDirection = direction === "desc" ? "DESC" : "ASC";
        if (property) {
          order.push([property, sortDirection]);
        }
      }
      const queryOptions = {
        attributes: ["id", "roomType", "roomNumber", "price", "availability", "services", "imageRoom", "optionGuestAndChildNumber", "roomName", "roomEveduate", "checkIn", "checkOut", "createdAt", "updatedAt", "isStayed", "isCheckOut", "monthCheckIn", "monthCheckOut"],
        where: whereClause,
        order
      };
      if (start) {
        queryOptions.offset = parseInt(start, 10);
      }
      if (limit) {
        queryOptions.limit = parseInt(limit, 10);
      }
      const RoomList = await db.Rooms.findAndCountAll(queryOptions);
      resolve({
        data: RoomList.rows,
        count: RoomList.count
      });
    } catch (e) {
      reject(e);
    }
  });
};
const deleteRoom = roomId => {
  return new Promise(async (resolve, reject) => {
    try {
      const errObj = {};
      const getRoomById = await db.Rooms.findOne({
        where: {
          id: roomId
        }
      });
      if (!getRoomById) {
        errObj.code = 1, errObj.message = "Room not found";
        resolve(errObj);
      }
