const db = require("../models/index");
const ExcelJS = require("exceljs");
const { Op } = require("sequelize");

const generateExcelData = async (filter) => {
  const whereClause = {};

  if (filter) {
    const filterArray = JSON.parse(filter);
    if (Array.isArray(filterArray)) {
      filterArray.forEach(({ operator, value, property }) => {
        if (operator && value && property) {
          whereClause[property] = {
            [Op[operator]]: value,
          };
        }
      });
    }
  }

  const filterRooms = await db.Rooms.findAll({ where: whereClause });

  const workbook = new ExcelJS.Workbook();
  const worksheet = workbook.addWorksheet("Rooms");

  if (filterRooms) {
    worksheet.columns = [
      { header: "Tn phng", key: "roomName", width: 300 },
      { header: "Loi phng", key: "roomType", width: 300 },
      { header: "S phng", key: "roomNumber", width: 300 },
      { header: "Gi phng", key: "price", width: 300 },
      { header: "Trng thi", key: "availability", width: 300 },
      { header: "ang ", key: "isStayed", width: 300 },
      { header: " check out", key: "isCheckOut", width: 300 },
    ];

    filterRooms.forEach((room) => {
      worksheet.addRow({
        roomName: room.roomName,
        roomType: room.roomType,
        roomNumber: room.roomNumber,
        price: room.price,
        availability: room.availability === 0 ? "Cha t" : " t",
        isStayed: room.isStayed === 1 ? "C" : "Khng",
        isCheckOut: room.isCheckOut === 1 ? "C" : "Khng",
      });
    });
  }

  return workbook;
};

const generateExcelRoomOrdersData = async (filter) => {
  const whereClause = {};

  if (filter) {
    const filterArray = JSON.parse(filter);
    if (Array.isArray(filterArray)) {
      filterArray.forEach(({ operator, value, property }) => {
        if (operator && value && property) {
          whereClause[property] = {
            [Op[operator]]: value,
          };
        }
      });
    }
  }

  const filterOrderRooms = await db.Orders.findAll({ where: whereClause });

  const workbook = new ExcelJS.Workbook();
  const worksheet = workbook.addWorksheet("Rooms");

  if (filterOrderRooms) {
    worksheet.columns = [
      { header: "Tn khch hng", key: "customerName", width: 300 },
      { header: "S lng phng", key: "infoBooking", width: 300 },
      { header: "Check in", key: "checkIn", width: 300 },
      { header: "Check out", key: "checkOut", width: 300 },
      { header: "Trng thi", key: "isPayment", width: 300 },
      { header: "Tng gi phng", key: "realPrice", width: 300 },
    ];

    filterOrderRooms.forEach((room) => {
      worksheet.addRow({
        customerName: room.customerName,
        infoBooking: room.infoBooking.length,
        checkIn: room.checkIn,
        checkOut: room.checkOut,
        realPrice: room.realPrice,
        isPayment: room.isPayment === 0 ? "Cha thanh ton" : " thanh ton",
      });
    });
  }

  return workbook;
};

const generateExcelRoomByDate = async ({ checkIn, checkOut }) => {
  const dataArr = {};

  dataArr.checkIn = checkIn;
  dataArr.checkOut = checkOut;

  console.log(dataArr.checkIn + "-" + dataArr.checkOut);

  const bookingsOrder = await db.Orders.findOne({
    where: {
      checkIn: dataArr.checkIn,
      checkOut: dataArr.checkOut,
    },
  });

  console.log(bookingsOrder);

  const workbook = new ExcelJS.Workbook();
  const worksheet = workbook.addWorksheet("Rooms");

  if (bookingsOrder) {
    worksheet.columns = [
      { header: "Tn phng", key: "roomName", width: 300 },
      { header: "Loi phng", key: "roomType", width: 300 },
      { header: "S phng", key: "roomNumber", width: 300 },
      { header: "Gi phng", key: "price", width: 300 },
      { header: "Ngy t", key: "checkIn", width: 300 },
      { header: "Ngy tr phng", key: "checkOut", width: 300 },
      { header: "Trng thi", key: "availability", width: 300 },
    ];

    bookingsOrder.infoBooking.forEach((room) => {
      worksheet.addRow({
        roomName: room.roomName,
        roomType: room.roomType,
        roomNumber: room.roomNumber,
        price: room.price,
        checkIn: dataArr.checkIn,
        checkOut: dataArr.checkOut,
        availability: room.availability === 0 ? "Cha t" : " t",
      });
    });
  }

  return workbook;
};

module.exports = {
  generateExcelData: generateExcelData,
  generateExcelRoomOrdersData: generateExcelRoomOrdersData,
  generateExcelRoomByDate: generateExcelRoomByDate,
};

